from django.conf.urls import url, include
from django.views.generic import ListView, DetailView, RedirectView, TemplateView


urlpatterns = [
    url(r'^$', TemplateView.as_view(template_name='addressbook/index.html'), name='addressbook_index'),
    url(r'^group/add$', TemplateView.as_view(template_name='add_group'), name='addressbook_add_group'),
    url(r'^contact/add$', TemplateView.as_view(template_name='add_contact'), name='addressbook_add_contact'),    
    url(r'^contact/(?P<pk>\d+)/edit$', TemplateView.as_view(template_name='edit_contact'), name='addressbook_edit_contact'),    
    url(r'^contact/(?P<pk>\d+)/view$', TemplateView.as_view(template_name='single_contact'), name='addressbook_single_contact'),    
    url(r'^contact/(?P<pk>\d+)/download$', TemplateView.as_view(template_name='download_vcard'), name='addressbook_download_vcard'),    
]